import React, { useState } from "react";
import BlogListing from "../blog/listing";
import InsertForm from "../blog/insert";

const Blog = () => {
  const [Blogs, setBlog] = useState([
    { author: "Author 1", content: "Blog content 1" },
    { author: "Author 2", content: "Blog content 2" },
  ]);

  const addBlog = (blog) => {
    console.log(blog);
    setBlog(...Blogs, blog);
  };

  return (
    <div className="">
      <br />
      <br />
      <InsertForm addblog={addBlog} />
      {/* {Blogs.map((item, key) => (
        <BlogListing blog={item} key={key}></BlogListing>
      ))} */}
    </div>
  );
};

export default Blog;
