import React, { useState } from "react";
import BlogListing from "../blog/listing";
import InsertForm from "../blog/insert";
import { Link } from "react-router-dom";
<Link to={`/blog/${item.title}`}>Read More</Link>

const Blog = () => {
  const [Blogs, setBlog] = useState([
    { author: "Author 1", content: "Blog content 1", title: "Some Title" },
  ]);

  const addBlog = (blog) => {
    setBlog((prevBlogs) => [...prevBlogs, blog]);
  };

  return (
    <div className="">
      <br />
      <br />
      <InsertForm addblog={addBlog} />
      {Blogs.map((item, key) => (
        <BlogListing blog={item} key={key}></BlogListing>
      ))}
    </div>
  );
};

export default Blog;
