  } 

  return (
    <div class="container">
      <form>
        <h2>Create Blog</h2>
        <div class="row">
          <div class="col-md-4">
            <div class="div-group">
              <label for="first">Auther Name</label>
              <input
                type="text"
                class="form-control"
                placeholder=""
                name="auther"
                value={formData.auther}
                autoComplete="off"
                onChange={handleChange}
                id="first"
              />
            </div>
          </div>

          <div class="col-md-12 mt-5">
            <div class="form-group">
              <label for="last">Insert Text</label>
              <textarea
                name="content"
                id=""
                onChange={handleChange}
                cols="30"
                placeholder="Dummy Text"
                class="form-control"
                rows="5"
              >{formData.content}</textarea>
            </div>
          </div>
        </div>

        <button type="submit" class="mt-4 btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
};

export default InsertForm;
